import React, { useEffect, useState } from "react";
import Weather from "../Weather/Weather.js";
import { getWeatherList } from "../../Api.js";
import "./Weathers.css";

const Weathers = (props) => {
    const [datas, setDatas] = useState([]);

    useEffect(() => {
        // Menangkap data result API dan menambahkan penanganan kesalahan
        getWeatherList()
            .then((result) => {
                setDatas(result);
            })
    }, []);

    console.log(datas);
    function updateTime() {
        var now = new Date();
        var day = now.getDate();
        var month = now.getMonth() + 1;
        var year = now.getFullYear();

        // Mengubah bulan dan hari ke format string
        month = month < 10? "0" + month : month;
        day = day < 10? "0" + day : day;

        var timeString = day + "-" + month + "-" + year;
        document.getElementById("waktu").innerText = timeString;
      }

      // Memperbarui waktu setiap 1 detik
      setInterval(updateTime, 1000);

    return (
        <div className="container">
            <h2>Perkiraan Cuaca Hari Ini <p id="waktu"></p> di seluruh Indonesia</h2>
            <div class="movies-container">
                {datas.map((data) => {
                    return (
                        <Weather
                            id={data.id}
                            propinsi={data.propinsi}
                            kota={data.kota}
                            kecamatan={data.kecamatan}
                            lat={data.lat}
                            lon={data.lon}
                        />
                    );
                })}
            </div>
        </div>
    );
};

export default Weathers;
