import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Detail = () => {
  const { id } = useParams();
  const [weather, setWeather] = useState();

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const weatherData = await getDetailWeather(id);
        setWeather(weatherData);
      } catch (error) {
        console.error("Error fetching weather:", error);
      }
    };

    fetchWeather();
  }, [id]);

  if (!weather) {
    return <div>Loading...</div>;
  }

  const { jamCuaca, kodeCuaca, cuaca, humidity, tempC, tempF } = weather;

  return (
    <div>
      <Navbar />
      <p>Parameter id: {id}</p>
      <p>Jam Cuaca :{jamCuaca}</p>
      <p>Kode Cuaca :{kodeCuaca}</p>
      <p>Cuaca : {cuaca}</p>
      <p>Humidity: {humidity}</p>
      <p>Temperature (Celsius): {tempC}</p>
      <p>Temperature (Fahrenheit): {tempF}</p>
      <Footer />
    </div>
  );
};

export default Detail;
