import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
Nav

const Detail = () => {
  const { id } = useParams();
  const [weather, setWeather] = useState([]);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const weatherData = await getDetailWeather(id);
        setWeather(weatherData);
      } catch (error) {
        console.error("Error fetching weather:", error);
      }
    };

    fetchWeather();
  }, [id]);

  if (!weather.length) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Navbar />
      <p>Parameter id: {id}</p>
      {weather.map((item, index) => (
        <div key={index}>
          <p>Jam Cuaca :{item.jamCuaca}</p>
          <p>Kode Cuaca :{item.kodeCuaca}</p>
          <p>Cuaca : {item.cuaca}</p>
          <p>Humidity: {item.humidity}</p>
          <p>Temperature (Celsius): {item.tempC}</p>
          <p>Temperature (Fahrenheit): {item.tempF}</p>
        </div>
      ))}
      <Footer />
    </div>
  );
};

export default Detail;

async function getDetailWeather(id) {
  const response = await fetch(`https://ibnux.github.io/BMKG-importer/cuaca/${id}.json`);
  return response.json();
}
