import React, { useEffect, useState } from "react";
import Weather from "../Weather/Weather.js";
import { getWeatherList } from "../../Api.js";

const Weathers = () => {
    const [datas, setDatas] = useState([]);
    const [error, setError] = useState(null); // State untuk menyimpan kesalahan

    useEffect(() => {
        // Menangkap data result API dan menambahkan penanganan kesalahan
        getWeatherList()
            .then((result) => {
                setDatas(result);
            })
            .catch((error) => {
                setError(error);
            });
    }, []);

    console.log(datas);

    if (error) {
        return <div>Terjadi kesalahan: {error.message}</div>;
    }

    return (
        <div className="container">
            <div class="movies-container">
                {datas.map((data, index) => {
                    return (
                        <Weather
                            key={index} // Tambahkan prop key untuk masing-masing elemen dalam loop
                            id={data.id}
                            provinsi={data.provinsi}
                            kota={data.kota}
                            kecamatan={data.kecamatan}
                            lat={data.lat}
                            lon={data.lon}
                        />
                    );
                })}
            </div>
        </div>
    );
};

export default Weathers;
