import React, { useEffect, useState } from "react";
import Weather from "../Weather/Weather.js";
import { getWeatherList } from "../../Api.js";
import "./Weathers.css";

const Weathers = (props) => {
    const [datas, setDatas] = useState([]);

    useEffect(() => {
        // Menangkap data result API dan menambahkan penanganan kesalahan
        getWeatherList()
            .then((result) => {
                setDatas(result);
            })
    }, []);

    console.log(datas);
    const days = ['Minggu', 'Senin', 'Selasa', 'Rabu', 'Kamis', 'Jumat', 'Sabtu'];
    const months = ['Januari', 'Februari', 'Maret', 'April', 'Mei', 'Juni', 'Juli', 'Agustus', 'September', 'Oktober', 'November', 'Desember'];

    const now = new Date();
    const weekDay = days[now.getDay()];
    const day = now.getDate();
    const month = months[now.getMonth()];
    const year = now.getFullYear();
    const hours = now.getHours();
    const minutes = now.getMinutes();
    const seconds = now.getSeconds();

    const formatDate = `${weekDay}, ${day} ${month} ${year}`;
    const formatTime = `${hours}:${minutes}:${seconds}`;

    console.log(`Tanggal dan hari terkini: ${formatDate}`);
    console.log(`Waktu terkini: ${formatTime}`);

    return (
        <div className="container">
            <h2>Perkiraan Cuaca Hari Ini di seluruh Indonesia</h2>
            <div class="movies-container">
                {datas.map((data) => {
                    return (
                        <Weather
                            id={data.id}
                            propinsi={data.propinsi}
                            kota={data.kota}
                            kecamatan={data.kecamatan}
                            lat={data.lat}
                            lon={data.lon}
                        />
                    );
                })}
            </div>
        </div>
    );
};

export default Weathers;
