import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../../component/Navbar/Navbar';
import Footer from '../../component/Footer/Footer';
import { getDetailWeather } from '../../Api';
import './Detail.css';


const Detail = () => {
  const { id } = useParams();
  const [weather, setWeather] = useState([]);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const weatherData = await getDetailWeather(id);
        setWeather(weatherData);
      } catch (error) {
        console.error("Error fetching weather:", error);
      }
    };

    fetchWeather();
  }, [id]);


  return (
    <div >
      <Navbar />
      <p>Parameter id: {id}</p>
      {weather.map((item, index) => (
        <div class="card-1">
          <div className='card-detail' key={index}>
            <h3> {item.cuaca}</h3>
            {item.kodeCuaca === '0' && <img src="https://ibnux.github.io/BMKG-importer/icon/0.png" alt="Weather Icon" />}
            {item.kodeCuaca === '1' && <img src="https://ibnux.github.io/BMKG-importer/icon/1.png" alt="Weather Icon" />}
            {item.kodeCuaca === '2' && <img src="https://ibnux.github.io/BMKG-importer/icon/2.png" alt="Weather Icon" />}
            {item.kodeCuaca === '3' && <img src="https://ibnux.github.io/BMKG-importer/icon/3.png" alt="Weather Icon" />}
            {item.kodeCuaca === '4' && <img src="https://ibnux.github.io/BMKG-importer/icon/4.png" alt="Weather Icon" />}
            {item.kodeCuaca === '4' && <img src="https://ibnux.github.io/BMKG-importer/icon/4.png" alt="Weather Icon" />}
            <p> {item.tempC} °C</p>
            <p> {item.tempF} °F</p>
            <p>Kode Cuaca :{item.kodeCuaca}</p>
            <p>Kelembapan: {item.humidity} RH</p>
            <p> {item.jamCuaca}</p>
          </div>
        </div>
      ))}
      <Footer />
    </div>
  );
};

export default Detail;
