import Navbar from "../Navbar/Navbar.js";
import Footer from "../Footer/Footer.js";
import Weathers from "../Weathers/Weathers.js";
import "./Main.css";

const Main = () => {
    function updateTime() {
        var now = new Date();
        var year = now.getFullYear();
        var month = now.getMonth() + 1;
        var day = now.getDate();
        var dayName = now.getDay();
        var hours = now.getHours();
        var minutes = now.getMinutes();
        var seconds = now.getSeconds();

        // Mengubah bulan dan hari ke format string
        month = month < 10? "0" + month : month;
        day = day < 10? "0" + day : day;

        // Mengubah hari ke nama hari
        var dayNames = ["Minggu", "Senin", "Selasa", "Rabu", "Kamis", "Jumat", "Sabtu"];
        dayName = dayNames[dayName];

        var timeString = dayName + ", " + day + "-" + month + "-" + year + " " + hours + ":" + minutes + ":" + seconds;
        document.getElementById("waktu").innerText = timeString;
      }

      // Memperbarui waktu setiap 1 detik
      setInterval(updateTime, 1000);
    return (
        <div class="content">
            <p id="waktu"></p>
            <Navbar />
            <Weathers />
            <Footer />
        </div>
    )
}

export default Main;
