import React, { useEffect, useState } from "react";
import Weather from "../Weather/Weather.js";
import { getWeatherList } from "../../Api.js";
import "./Weathers.css";

const Weathers = (props) => {
    const [datas, setDatas] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");

    useEffect(() => {
        // Menangkap data result API dan menambahkan penanganan kesalahan
        getWeatherList()
            .then((result) => {
                setDatas(result);
            })
    }, []);

    console.log(datas);

    const filteredData = datas.filter((data) =>
    data.kota.toLowerCase().includes(searchTerm.toLowerCase())
  );

    return (
        <div className="container-fluid">
            <h2 style={{fontFamily:"fantasy"}}>Perkiraan Cuaca Hari Ini di seluruh Indonesia</h2>
            <input
        type="text"
        id="search"
        placeholder="Search..."
        onChange={(e) => setSearchTerm(e.target.value)}
      />
            <div className="weather">
                {datas.map((data) => {
                    return (
                        <Weather
                            id={data.id}
                            propinsi={data.propinsi}
                            kota={data.kota}
                            kecamatan={data.kecamatan}
                            // lat={data.lat}
                            // lon={data.lon}
                        />
                    );
                })}
            </div>
        </div>
    );
};

export default Weathers;
