import React, { useState } from "react";
import './AddWeatherForm.css';

const AddWeatherForm = ({ onAddWeather }) => {
    const [formData, setFormData] = useState({
        cuaca: "",
        tempC: "",
        tempF: "",
        humidity: "",
        jamCuaca: "",
        kodeCuaca: "",
    });

    const handleChange = (event) => {
        const selectedIcon = event.target.value;
        console.log('Selected icon:', selectedIcon);
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddWeather(formData);
    };

    const options = [
        { value: '1', label: 'Option 1' },
        { value: '2', label: 'Option 2' },
        { value: '3', label: 'Option 3' },
        // Add more options here
      ];

      const Detail = ({ id }) => {
        const [datas, setDatas] = useState([]);
        const [selectedOption, setSelectedOption] = useState('');

        useEffect(() => {
          fetchWeather();
        }, [id]);

        const handleSelectChange = (event) => {
          setSelectedOption(event.target.value);
        };
    }
        return (
            <div className="container-2">
                <section className="form">
                    {/* <div className="form-left">
                    <img src="https://picsum.photos/200/300?random=1" alt="img-form" />
                </div> */}
                    <div className="form-right">
                        <form onSubmit={handleSubmit}>
                            <div>
                                <label className="label" for="provinsi">Cuaca</label>
                                <input className="input" type="text" id="cuaca" name="cuaca" onChange={handleChange} />
                            </div>

                            <div>
                                <label className="label" for="kota">Fahrenhit</label>
                                <input className="input" type="text" id="tempF" name="tempF" onChange={handleChange} />
                            </div>
                            <div>
                                <label className="label" for="kecamatan">Celcius</label>
                                <input className="input" type="text" id="tempC" name="tempC" onChange={handleChange} />
                            </div>
                            <div>
                                <label className="label" for="kecamatan">Kelembapan</label>
                                <input className="input" type="text" id="humidity" name="humidity" onChange={handleChange} />
                            </div>
                            <div>
                                <label className="label" for="kecamatan">Waku Cuaca</label>
                                <input className="input" type="time" id="jamCuaca" name="jamCuaca" onChange={handleChange} />
                            </div>
                            <div>
                                <label className="label" for="kecamatan">Kode Cuaca</label>
                                <select value={selectedOption} onChange={handleSelectChange}>
                                    <option value="">Select an option</option>
                                    {options.map((option) => (
                                        <option key={option.value} value={option.value}>
                                            {option.label}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            <button className="button" type="submit">Submit</button>
                        </form>
                    </div>
                </section>
            </div>
        )
    }

    export default AddWeatherForm;
