import React, { useState } from "react";
import './AddWeatherForm.css';

const AddWeatherForm = ({ onAddWeather }) => {
    const [formData, setFormData] = useState({
        provinsi: "",
        kota: "",
        kecamatan: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddWeather(formData);
    }
    return (
        <div className="container-2">
            <section className="form">
                <div className="form-left">
                    <img src="https://picsum.photos/200/300?random=1" alt="img-form" />
                </div>
                <div className="form-right">
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label className="label" for="provinsi">Provinsi:</label>
                            <input type="text" id="provinsi" name="provinsi" onChange={handleChange}  />
                        </div>

                        <div>
                            <label className="label" for="kota">Kota:</label>
                            <input type="text" id="kota" name="kota" onChange={handleChange} />
                        </div>

                        <div>
                            <label className="label" for="kecamatan">Kecamatan:</label>
                            <input type="text" id="kecamatan" name="kecamatan" onChange={handleChange} />
                        </div>

                        <button type="submit">Submit</button>
                    </form>
                </div>
            </section>
        </div>
    )
}

export default AddWeatherForm;
