import React, { useState } from "react";
import './AddWeatherForm.css';

const AddWeatherForm = ({ onAddWeather}) => {
    const [formData, setFormData] = useState({
        provinsi: "",
        kota: "",
        kecamatan: "",
    });

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddWeather(formData);
    }
    return (
        <div className="container-2">
            <section className="form">
                <div className="form-left">
                    <img src="https://picsum.photos/200/300?random=1" alt="img-form" />
                </div>
                <div className="form-right">
                    <form action="" onSubmit={handleSubmit}> 
                        <div className="form-inputgroup">
                            <label htmlFor="provinsi" className="form-label" for="">provinsi</label>
                            <input type="text" name="provinsi" id="provinsi" className="form-input" onChange={handleChange} />
                        </div>
                        <div class="form-inputgroup">
                            <label htmlFor="kota" className="form-label" for="">kota</label>
                            <input type="text" name="kota" id="kota" className="form-input" onChange={handleChange} />
                        </div>

                        <div class="form-inputgroup">
                            <label htmlFor="kecamatan" className="form-label" for="">kecamatan</label>
                            <input type="text" name="kecamatan" id="kecamatan" className="form-input" onChange={handleChange} />
                        </div>
                        <button type="submit" className="form-button">Kirim</button>
                    </form>
                </div>
            </section>
        </div>
    )
}

export default AddWeatherForm;
